package main

var cmdTemplate = `
// Code generated by gen/main.go; DO NOT EDIT.

package cmd

import (
	"context"
	"fmt"

	"github.com/auifzysr/yabqsqcli/pkg/config"
	"github.com/auifzysr/yabqsqcli/pkg/factory"
	"github.com/urfave/cli/v2"
)

func {{ .Name }}(cfg *config.{{ .Name | capitalize }}Config) error {
	tc, err := factory.{{ .Name | capitalize }}TransferConfigFactory(cfg)
	if err != nil {
		return err
	}
	ctx := context.Background()

	m, err := client.{{ .Name | capitalize }}TransferConfig(ctx, tc)
	if err != nil {
		return fmt.Errorf("{{ .Name }} transfer failed: parent: %s, %w", fmt.Sprintf("projects/%s/locations/%s",
			cfg.ProjectID, cfg.Region,
		), err)
	}
	fmt.Printf("meta: %+v", m)
	return nil
}

func {{ .Name }}Command(rootCfg *config.RootConfig) *cli.Command {
	cfg := &config.{{ .Name | capitalize }}Config{
		RootConfig: rootCfg,
	}

	return &cli.Command{
		Name:    "{{ .Name }}",
		Aliases: []string{"c"},
		Usage:   "{{ .Name }} scheduled query config",
		Action: func(cCtx *cli.Context) error {
			return {{ .Name }}(cfg)
		},
		Flags: []cli.Flag{
		{{ .FlagTemplate }}
		},
	}
}
`
